package day3j;

public class Array {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int arr[]=new int[] {1,2,3,4,5};
		String n[]= {"kiru","sher","vin","joe"};
		System.out.println(arr.length);
		for(int i:arr) {
			System.out.println(i);
		}
		for(String j:n) {
			System.out.println(j);
		}
	}

}
